query:
mutation adminAddServicePrincipalAdmin ( $accountId:ID! $addServicePrincipalAdminInput:AddServicePrincipalAdminInput!) {
	admin ( accountId:$accountId ) {
		addServicePrincipalAdmin ( input:$addServicePrincipalAdminInput  )  {
adminID {
	id
	status
	version
	role
	firstName
	lastName
	email
	creationDate
	modifyDate
	passwordNeverExpires
	mfaEnabled
	nativeAccountID
	presentUsageAndEvents
	adminType
	allowedItems {
		id
		name
		type
	}
	managedRoles {
		allowedAccounts
		role {
			id
			name
			description
			isPredefined
		}
		allowedEntities {
			id
			name
			type
		}
	}
	resellerRoles {
		allowedAccounts
		role {
			id
			name
			description
			isPredefined
		}
		allowedEntities {
			id
			name
			type
		}
	}
}
		}
	}	
}
