query:
mutation siteRemoveSocketAddOnCard ( $accountId:ID! $removeSocketAddOnCardInput:RemoveSocketAddOnCardInput!) {
	site ( accountId:$accountId ) {
		removeSocketAddOnCard ( input:$removeSocketAddOnCardInput  )  {
			addOns {
				expansionSlotNumber
	type {
		name
		recommendedAction
		details
	}
			}
		}
	}	
}
