components.css_content:
@import url('https://fonts.googleapis.com/css2?family=Inter:wght@400;600;700&display=swap');
* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
}
html, body {
  width: 1080px;
  height: 1350px;
  font-family: 'Inter', Arial, sans-serif;
  background: ${bg_color};
  text-align: center;
}
body {
  display: flex;
  align-items: center;
  justify-content: center;
}
.poster {
  width: 100%;
  height: 100%;
  padding: 60px 80px;
  background: ${bg_color};
  display: flex;
  flex-direction: column;
}
.header {
  display: flex;
  justify-content: center;
  align-items: center;
  min-height: 80px;
}
.header img {
  height: 55px;
  object-fit: contain;
}
.content {
  flex: 1;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  gap: 60px;
}
.title {
  font-size: 48px;
  font-weight: 700;
  letter-spacing: 6px;
  color: ${title_color};
}
.count-container {
  display: flex;
  gap: 40px;
}
.count-box {
  width: 280px;
  height: 360px;
  border-radius: 30px;
  background: ${count_box_color};
  display: flex;
  align-items: center;
  justify-content: center;
  box-shadow: 0 30px 60px rgba(0, 0, 0, 0.12);
}
.count-box span {
  font-size: 200px;
  font-weight: 700;
  color: ${count_text_color};
  line-height: 1;
}
.subtext {
  font-size: 32px;
  font-weight: 400;
  letter-spacing: 4px;
  color: ${description_color};
}
.footer {
  margin-top: auto;
  padding-top: 40px;
  font-size: 20px;
  color: ${footer_text_color};
  line-height: 1.6;
}
.footer div + div {
  margin-top: 6px;
}
${custom_css}
