components.css_content:
body {
  margin: 0;
  padding: 0;
  background: ${background_color};
  display: flex;
  justify-content: center;
  align-items: center;
  height: 100vh;
}

.post {
  width: 1080px;
  height: 1080px;
  background: ${background_color};
  padding: 80px;
  box-sizing: border-box;
  font-family: "Georgia", serif;
  text-align: center;
  color: ${text_color};
}

.logo {
  display: flex;
  align-items: center;
  justify-content: center;
  margin-bottom: 20px;
}

.logo img {
  height: 30px;
  width: auto;
  object-fit: contain;
}

.heading-small {
  font-style: italic;
  font-size: 48px;
  margin-top: 40px;
  color: ${accent_secondary_color};
}

.heading-large {
  font-size: 72px;
  font-weight: 500;
  line-height: 1.1;
  margin: 20px 0;
  color: ${accent_primary_color};
}

.website {
  font-size: 28px;
  letter-spacing: 2px;
  margin-top: 10px;
  margin-bottom: 80px;
  color: ${accent_secondary_color};
}

.resource-container {
  width: 100%;
  max-width: 900px;
  aspect-ratio: 16/9;
  margin: 0 auto;
  background: ${resource_container_background};
  display: flex;
  justify-content: center;
  align-items: center;
  overflow: hidden;
  border-radius: 4px;
}

.resource-container img {
  width: 100%;
  height: 100%;
  object-fit: cover;
}

${custom_css}
