components.css_content:
body {
    margin: 0;
    padding: 30px;
    width: 1080px;
    height: 1080px;
    background: ${background_color};
    font-family: Arial, sans-serif;
    color: ${text_color};
    display: flex;
    flex-direction: column;
    align-items: center;
    overflow: hidden;
    position: relative;
}

/* Header */
.header {
    margin-top: 60px;
    flex-shrink: 0;
}
.header img {
    height: 40px;
}

/* Main content container - vertically centered */
.main-content {
    flex: 1;
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    width: 100%;
}

/* Title */
.title {
    font-size: 54px;
    font-weight: bold;
    text-align: center;
    line-height: 1.3;
    max-width: 90%;
    margin-bottom: 80px;
    white-space: pre-line;
}

/* Options container */
.options-box {
    display: flex;
    justify-content: center;
    align-items: stretch;
    background: ${options_box_background};
    border-radius: 40px;
    width: 800px;
    height: 140px;
    overflow: hidden;
    border: 1px solid ${options_box_border};
}

.option {
    flex: 1;
    display: flex;
    justify-content: center;
    align-items: center;
    font-size: 36px;
    font-weight: bold;
}

.divider {
    width: 2px;
    background: ${divider_color};
}

/* Footer */
.footer {
    margin-bottom: 60px;
    font-size: 26px;
    opacity: 0.9;
    flex-shrink: 0;
}
${custom_css}
