components.css_content:
@import url('https://fonts.googleapis.com/css2?family=Poppins:wght@100;200;300;400;600;700;900&display=swap');

@font-face {
  font-family: 'Horizon';
  src: url('Horizon.ttf') format('truetype');
  font-weight: 900;
}

body {
  margin: 0;
  padding: 0;
  background: ${background_color};
  display: flex;
  justify-content: center;
  align-items: center;
  height: 100vh;
}

.post {
  width: 1080px;
  height: 1080px;
  background: ${background_color};
  color: ${accent_secondary_color};
  box-sizing: border-box;
  padding: 60px;
  text-align: center;
  position: relative;
  overflow: hidden;
  font-family: 'Poppins', sans-serif;
}

.spotlight-top,
.spotlight-bottom {
  position: absolute;
  left: 50%;
  transform: translateX(-50%);
  width: 900px;
  height: 300px;
  background: radial-gradient(ellipse at center, rgba(255, 255, 255, 0.35) 0%, rgba(0, 0, 0, 0) 70%);
  filter: blur(20px);
}

.spotlight-top {
  top: 0;
}

.spotlight-bottom {
  bottom: 0;
}

.logo {
  display: flex;
  align-items: center;
  justify-content: center;
  gap: 12px;
  font-size: 32px;
  margin-top: 40px;
}

.logo img {
  width: 180px;
  height: 180px;
  object-fit: contain;
}

.big-text {
  font-family: 'Horizon', sans-serif;
  font-size: 170px;
  font-weight: 900;
  margin-top: 40px;
  margin-bottom: -20px;
  line-height: 1;
}

.big-text.small {
  font-size: 150px;
  margin-top: -20px;
}

.ribbon {
  margin: 25px auto;
  background: ${accent_primary_color};
  padding: 8px 40px;
  width: 60%;
  font-size: 34px;
  font-weight: 600;
  letter-spacing: 6px;
}

.desc {
  font-size: 30px;
  margin-top: 30px;
  max-width: 80%;
  margin-left: auto;
  margin-right: auto;
  line-height: 1.5;
}

.website {
  margin-top: 50px;
  font-size: 20px;
  letter-spacing: 2px;
  font-weight: 100;
}

${custom_css}
