# Font registry
FONTS = {
    "block": BlockFont.DIGITS,
    "slim": SlimFont.DIGITS,
    "dot": DotMatrixFont.DIGITS,
    "bold": BoldFont.DIGITS,
    "mini": MinimalFont.DIGITS,
}

# Default font
DEFAULT_FONT = "block"


def get_font(name: str) -> dict[str, list[str]]:
    """Get font by name, fallback to default if not found."""
    return FONTS.get(name, FONTS[DEFAULT_FONT])


def list_fonts() -> list[str]:
    """Get list of available font names."""
    return list(FONTS.keys())
